<?	header('Content-type: text/plain; charset=UTF-8');	include('../data_src/catalog.php');	$lines = file('css.txt');	$map = array();	foreach($lines as $line){		if (preg_match('!\.(E[0-9A-F]{3}) \{ background-position\: 0px (-?\d+)px!', $line, $m)){			$map[hexdec($m[1])] = $m[2];		}	}	echo ".emoji { float:left; background: url(\"iphone_emoji.png\") top left no-repeat; width: 20px; height: 20px }\n";	foreach ($catalog as $item){		$uni = $item['unicode'];		$pos = $map[$item['softbank']['unicode']];		$unilow = sprintf('%x', $uni);		if (isset($pos)){			echo ".emoji$unilow { background-position: 0px {$pos}px; }\n";		}else{			echo ".emoji$unilow { background-position: 0px -9200px; } /* placeholder */\n";		}	}?>